        scanf("%s",ch);
        int len=strlen(ch);
        for(int i=0;i<len;++i)
        t[len-i-1]=ch[i]-'0';
        return t;
    }
    struct nd{int ne,to;bitset<1024>w;}e[N*2];
    void in(int x,int y,bitset<1024>w)
    {e[++cnt].to=y;e[cnt].w=w;e[cnt].ne=head[x];head[x]=cnt;return;}
    int gf(int x){return fa[x]==x?x:fa[x]=gf(fa[x]);}
    void dfs(int x,int f)
    {
        for(int i=head[x];i;i=e[i].ne)
        if(e[i].to!=f)
        {
            dis[e[i].to]=dis[x]^e[i].w;  
            dfs(e[i].to,x);
        }
    }
    int update(int x,bitset<1024>y)
    {
        now=0;
        for(int i=0;i<N;++i)
        if(M[i][x]&&mat[i].none())
        {
            now=i;
            break;
        }
        if(!now)

        for(int i=0;i<N;++i)
        if(M[huan[i]][x]&&huan[i]!=0)
        {
            now=huan[i];
            huan[i]=0;
            break;
        }
        for(int i=0;i<N;++i)
        if(now!=i&&M[i][x])
        {
            mat[i]=mat[i]^mat[now];
            M[i]=M[i]^M[now];
        }
        mat[now]^=y;
        for(int i=N;i>=0;--i)
        if(mat[now][i])
        {
            if(!huan[i]){huan[i]=now;break;}
            else mat[now]=mat[now]^mat[huan[i]],M[now]=M[now]^M[huan[i]];
        }
        return 0;
    }
    void put()
    {
        bitset<1024>ans;
        ans.reset();
        for(int i=N-1;i>=0;--i)
        if(ans[i]==0&&huan[i])
        ans^=mat[huan[i]];
        char *tmp,tong[N];
        if(ans.none()) puts("0");
        else
        {
            tmp=tong;
            for(int i=N-1;i>=0;--i)
            if(ans[i])
            {
                for(int j=i;j>=0;--j)
                *(tmp++)=ans[j]+'0';
                break;
            }
            *(tmp++)='\0';  
            puts(tong);
        }
    }
    int main()
    {
        freopen("eights.in","r",stdin);
        freopen("eights.out","w",stdout);
        scanf("%d%d%d",&n,&m,&Q);
        for(int i=1;i<=n;++i)fa[i]=i;
        for(int i=1;i<N;++i)M[i][i]=1;
        for(int i=1,x,y;i<=m;++i)
        {
            scanf("%d%d",&x,&y);
            a=readb();
            if(gf(x)==gf(y))
            {
                e1[++cnt1][0]=x;
                e1[cnt1][1]=y;
                v[cnt1]=a;
            }
            else
            fa[gf(x)]=gf(y),in(x,y,a),in(y,x,a);
        }
        dfs(1,-1);
        int cntn=cnt1;
        for(int i=1;i<=cnt1;++i)
        update(i,dis[e1[i][0]]^dis[e1[i][1]]^v[i]);
        put();
        for(int i=1,x,y;i<=Q;++i)
        {
            scanf("%s",opt);
            if(opt[1]=='d')
            {
                scanf("%d%d",&x,&y);
                a=readb();
                e1[++cnt1][0]=x;e1[cnt1][1]=y,e2[cnt]=a;
                v[cnt1]=a;
                update(cnt1,dis[e1[cnt1][0]]^dis[e1[cnt1][1]]^v[cnt1]);
            }
            if(opt[1]=='h')
            {
                scanf("%d",&x);
                a=readb();
                update(x+cntn,v[x+cntn]^a);
                v[x+cntn]=a;
            }
            if(opt[1]=='a')
            {
                scanf("%d",&x);
                update(x+cntn,dis[e1[x+cntn][0]]^dis[e1[x+cntn][1]]^v[x+cntn]);
                v[x+cntn]=0;
            }
            put();
        }
    }

