        {
            if(!vis[i])	ans[i]=ans[i-1]*(i-1)%p*rev[i%p]%p;
            else				ans[i]=ans[i-1];
        }
        while(T--)
        {
            scanf("%d%d",&n,&m);
            printf("%d\n",fac[n]*ans[m]%p);
        }
    }

